func TestMozillaAuth0Initialize(t *testing.T) {
	// No trailing slash
	validator := MozillaAuth0Validator{"https://auth.mozilla.auth0.com", nil}
	err := validator.Initialize()
	assert.NotNil(t, err)

	validator = MozillaAuth0Validator{"https://auth.mozilla.auth0.com/", nil}
	err = validator.Initialize()
	assert.Nil(t, err)
}

